function setStepSize(array) {
  var stepSizeValue;
  if (Math.max.apply(null, array) < 10) {
    stepSizeValue = 1
  } else if (Math.max.apply(null, array) < 50) {
    stepSizeValue = 5
  } else if (Math.max.apply(null, array) < 100) {
    stepSizeValue = 10
  } else if (Math.max.apply(null, array) < 500) {
    stepSizeValue = 50
  } else {
    stepSizeValue = 100
  };
}

